function load () {
    textSprite = textsprite.create("Loading...")
    textSprite.x = 100
    loadingbar.z = 101
    for (let index = 0; index < 5; index++) {
        loadingbar.value += 200
        pause(500)
    }
    sprites.destroyAllSpritesOfKind(SpriteKind.loading, effects.fire, 500)
    textSprite.destroy()
    loadingbar.destroy()
}
