info.onLifeZero(function () {
    plane.setStayInScreen(false)
    plane.setVelocity(100, 100)
    sprites.destroyAllSpritesOfKind(SpriteKind.Player, effects.warmRadial, 2000)
    death_effect = randint(0, 1)
    timer.after(2000, function () {
        if (death_effect == 1) {
            game.over(false, effects.dissolve)
        } else if (death_effect == 2) {
            game.over(false, effects.slash)
        } else {
            game.over(false, effects.splatter)
        }
    })
})
