sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Enemy, function (sprite, otherSprite) {
    if (projectile.overlapsWith(enemy_fighter)) {
        enemy_fighter.destroy(effects.fire, 2000)
        enemy_plane += -1
        enemyshoot()
        info.player2.changeScoreBy(1)
    }
    if (projectile.overlapsWith(enemy_fighter_2)) {
        enemy_fighter_2.destroy(effects.fire, 2000)
        enemy_plane += -1
        enemyshoot()
        info.player1.changeScoreBy(1)
    }
    if (projectile.overlapsWith(enemy_fighter_3)) {
        enemy_fighter_3.destroy(effects.fire, 2000)
        enemy_plane += -1
        enemyshoot()
        info.player1.changeScoreBy(1)
    }
    if (projectile.overlapsWith(enemy_fighter_4)) {
        enemy_fighter_4.destroy(effects.fire, 2000)
        enemy_plane += -1
        enemyshoot()
        info.player1.changeScoreBy(1)
    }
    if (projectile.overlapsWith(v1)) {
        v1.setVelocity(0, 0)
        sprites.destroyAllSpritesOfKind(SpriteKind.Enemy, effects.ashes, 100)
        sprites.destroyAllSpritesOfKind(SpriteKind.Projectile, effects.ashes, 100)
        enemyshoot()
        info.player1.changeScoreBy(5)
    }
    if (projectile.overlapsWith(v12)) {
        v12.setVelocity(0, 0)
        sprites.destroyAllSpritesOfKind(SpriteKind.Projectile, effects.ashes, 100)
        sprites.destroyAllSpritesOfKind(SpriteKind.Enemy, effects.ashes, 100)
        enemyshoot()
        info.player1.changeScoreBy(5)
    }
    if (projectile.overlapsWith(v13)) {
        v13.setVelocity(0, 0)
        sprites.destroyAllSpritesOfKind(SpriteKind.Projectile, effects.ashes, 100)
        sprites.destroyAllSpritesOfKind(SpriteKind.Enemy, effects.ashes, 100)
        enemyshoot()
        info.player1.changeScoreBy(5)
    }
    if (projectile.overlapsWith(v14)) {
        v14.setVelocity(0, 0)
        sprites.destroyAllSpritesOfKind(SpriteKind.Enemy, effects.ashes, 500)
        sprites.destroyAllSpritesOfKind(SpriteKind.Projectile, effects.ashes, 500)
        enemyshoot()
        info.player1.changeScoreBy(5)
    }
})
